INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('really', 'adv', 'Did she really say so?', 1, '2019-10-09 18:49:52', '2019-10-09 18:49:52', '她真的那样说吗？');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('really', 'adv', 'Go ahead. Do whatever you really love.', 1, '2019-10-09 18:49:52', '2019-10-09 18:49:52', '去吧,去做你真正喜爱的事。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('really', 'adv', 'It''s really cold today.', 1, '2019-10-09 18:49:52', '2019-10-09 18:49:52', '今天可真冷。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('really', 'adv', 'It''s really too bad that the teacher couldn''t find someone to substitute for her when she was sick.', 1, '2019-10-09 18:49:52', '2019-10-09 18:49:52', '她生病时,老师找不到人代替她,实在太遗憾了。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('really', 'adv', 'They regard themselves as the best, and yet they don''t really seem to have a clear strategy.', 1, '2019-10-09 18:49:52', '2019-10-09 18:49:52', '他们自认为是最好的,而事实上他们缺乏清晰的战略。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('reason', 'n', 'He is able to explain the reason because he was present at that time.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '他当时在场,所以他能解释原因。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('reason', 'n', 'His assessment of the situation is based on sheer emotion, not reason.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '他对形势的评估完全是感情用事,而不是建立在理性基础上的。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('reason', 'n', 'That is the reason why I don''t like it.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '那就是我不喜欢它的原因。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('reason', 'n', 'We have reason to believe that he was murdered.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '我们有理由相信他是被谋杀的。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('reason', 'n', 'We shall in no way let reason be defeated by impulse.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '我们决不能让冲动战胜理智。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('reason', 'v', 'Copernicus reasoned that the earth revolved around the sun.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '哥白尼论证了地球绕着太阳转。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('reason', 'v', 'He reasoned that if we started at dawn, we would be there by noon.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '他推断，我们要是黎明出发，中午就能到。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('reason', 'v', 'Let us try to reason him into joining us.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '让我们来说服他和我们一起吧。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('reason', 'v', 'Who can reason with you when you are so stubborn?', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '你这么执拗的时候,谁能和你理论?');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('receive', 'v', 'He was received as an honoured visitor.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '他以贵宾的身分受到款待。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('receive', 'v', 'I was on vacation last week and didn''t receive your fax.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '上星期我正在度假，没收到你们的传真。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('receive', 'v', 'Neither John nor I am to receive the award.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '约翰和我谁都不会接到奖赏。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('recently', 'adv', 'Her books have gained popularity recently.', 1, '2019-10-09 18:49:52', '2019-10-09 18:49:52', '她的书近来大受欢迎。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('recently', 'adv', 'I guess from his red eyes that he has been working too hard recently.', 1, '2019-10-09 18:49:52', '2019-10-09 18:49:52', '我从他红红的眼睛猜想,他准是最近工作太辛苦了。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('recently', 'adv', 'We have twenty branches abroad, and we''ve recently opened a new one in Bangkok.', 1, '2019-10-09 18:49:52', '2019-10-09 18:49:52', '我们在海外有二十家分公司,最近在曼谷新开了一家。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('record', 'n', 'As a result of this meeting, he made his first record.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '由于这次会议,他制作了他的第一张唱片。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('record', 'n', 'He did very well, but failed to break the record.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '他做得很出色,但未破纪录。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('record', 'n', 'Her past record counts against her.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '她过去的经历对她不利。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('record', 'n', 'Last summer was the wettest on record.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '去年夏天是有记录以来降雨量最大的。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('record', 'n', 'She holds the world record for long distance swimming.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '她保持着长距离游泳的世界纪录。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('record', 'n', 'She seemed perversely proud of her criminal record.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '她似乎不通人事，拿自己的前科当荣耀。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('record', 'n', 'You should keep a record of your expenses.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '你应该记下你的各项开支。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('record', 'v', 'I recorded the score in a notebook.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '我在笔记本上记下了分数。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('record', 'v', 'The broadcast was recorded, not live.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '这次广播是事先录音的，不是现场直播。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('record', 'v', 'The thermometer recorded 40C.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '温度计上显示出40C.');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('record', 'v', 'We record our thoughts and experiences in diaries.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '我们把自己的思想和经验记录在日记中。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('refuse', 'n', 'The council is responsible for refuse collection.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '市政局负责清除垃圾废物。');
