INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('refuse', 'v', 'I refuse to answer that question.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '我不愿回答那个问题。');
