INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('regret', 'n', 'Much to my regret, I can''t accept your invitation.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '很抱歉,我不能接受你的邀请。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('regret', 'n', 'My only regret is that I have missed the chance to go abroad for further education.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '我唯一遗憾的是错过了去国外进修的机会。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('regret', 'n', 'There is no room for regret when matters have reached this stage.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '事已至此,后悔也没有用了。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('regret', 'v', 'I now regret having made a promise that I would join in the scheme.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '我现在后悔答应参加该计划的事。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('regret', 'v', 'We deeply regret that this big fire has taken away two young men''s lives.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '这次大火夺走了两个年轻人的生命,我们为此感到深深的惋惜。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('regret', 'v', 'We regret that we have to place the entire shipment at your disposal.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '很遗憾，我们只得将全部货品退回贵方自行处理。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('regulation', 'adj', 'Please use envelopes of the regulation size.', 1, '2019-10-09 18:49:51', '2019-10-09 18:49:51', '请用尺寸大小符合规定的信封。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('regulation', 'adj', 'She was fined for driving above the regulation speed.', 1, '2019-10-09 18:49:51', '2019-10-09 18:49:51', '她因超速开车而被罚款。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('regulation', 'n', 'From now on any violation of the regulation must be cruelly punished.', 1, '2019-10-09 18:49:51', '2019-10-09 18:49:51', '今后任何违反秩序的行为会受到严厉惩罚。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('regulation', 'n', 'It''s wrong of you to get around the regulation.', 1, '2019-10-09 18:49:51', '2019-10-09 18:49:51', '你存心规避规章制度是不对的。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('regulation', 'n', 'This regulation refers only to children.', 1, '2019-10-09 18:49:51', '2019-10-09 18:49:51', '这些规定仅适用于儿童。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('regulation', 'n', 'We need the regulation of share prices.', 1, '2019-10-09 18:49:51', '2019-10-09 18:49:51', '我们需要对股票价格的管制。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('relative', 'adj', 'Motion is absolute while stagnation is relative.', 1, '2019-10-09 18:49:58', '2019-10-09 18:49:58', '运动是绝对的,而静止是相对的。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('relative', 'adj', 'Relative to the size, the city is sparsely populated.', 1, '2019-10-09 18:49:58', '2019-10-09 18:49:58', '与其面积相比, 这个城市的人口是稀少的。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('relative', 'adj', 'The teacher asked me some questions relative to my paper.', 1, '2019-10-09 18:49:58', '2019-10-09 18:49:58', '老师问了我一些和我的论文有关的问题。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('relative', 'adj', 'They are now living in relative comfort.', 1, '2019-10-09 18:49:58', '2019-10-09 18:49:58', '他们现在过着比较安逸的生活。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('relative', 'n', 'He showed such concern that people took him to be a relative.', 1, '2019-10-09 18:49:58', '2019-10-09 18:49:58', '他很关心别人，因而大家都把他当作亲人。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('relax', 'v', 'A good massage will relax your tired muscles.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '舒服的按摩会让你疲惫的肌肉放松。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('relax', 'v', 'His muscles relaxed.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '他的肌肉松弛了。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('relax', 'v', 'I felt my shoulders relax a little.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '我觉得我的肩膀放松了好多。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('relax', 'v', 'I just want to sit down and relax.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '我只想坐下休息会。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('relax', 'v', 'The music will help to relax you.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '音乐会使你感到轻松。');
