INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('remain', 'n', 'After maintenance the main remains and remainders are left on the domain.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '维修之后，主要遗骸和剩余物留在了领地上。');
