INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('remain', 'v', 'Most of the unearthed relics remain intact.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '大多数出土文物仍保持完好无损。');
