INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('remain', 'v', 'Though the sore being healed, yet a scar may remain.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '伤口虽然痊愈, 却可能留下伤疤。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('remain', 'v', 'We must learn to conserve what remains.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '我们必须学会保存那些剩余的资源。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('remember', 'v', 'I really can''t remember what you said last night.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '我确实记不得你昨天晚上说了什么。');
