INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('remember', 'v', 'I shall always remember your kindness to us.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '我将永远记住你对我们的好。');
