INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('remember', 'v', 'Please remember me to your family.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '请代我问候你的家人。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('remember', 'v', 'Remember to write us when you get there.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '到了那儿别忘了给我们写信。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('remember', 'v', 'That was a beautiful summer, as I remember.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '记忆中,那是一个美丽的夏日。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('repair', 'n', 'He sent his car to the garage for repair.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '他把汽车送到修车厂修理。');
