INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('repair', 'v', 'I''ll have my watch repaired; it doesn''t work.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '我得修修表，它不走了。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('repair', 'v', 'She turned the chair on its side to repair it.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '她把椅子翻转过来修理。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('repair', 'v', 'The majority of the damage is easy to repair.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '这次的损害大部分容易补救。');
