INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('repeat', 'adj', 'You can also specify the repeat interval for the probe.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '你还可以指定探测器的重复间隔。');
