INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('shame', 'n', 'He had no sense of shame and never felt guilty.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '他毫无羞耻之心，而且从不觉得内疚。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('shame', 'n', 'He saved the child at the risk of his own life and put all those who looked on to shame.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '他冒着生命危险救了那个孩子,使所有旁观者都相形见绌。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('shame', 'n', 'It is a shame to take money from those who can''t afford it.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '拿别人的钱使人家无以自立是十分可耻的。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('shame', 'n', 'Your bad behavior brings shame on the whole school.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '你的恶劣行径给整个学校带来耻辱。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('shame', 'vt', 'He was shamed by how much more work the others had done.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '别人做得多得多，他感到很难为情。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('shame', 'vt', 'Her careful work put all the rest to shame.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '她的细致工作使得其余所有的人都自愧不如。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('shame', 'vt', 'Such an act of cowardice by an officer shames his whole regiment.', 1, '2019-10-09 18:49:57', '2019-10-09 18:49:57', '一个军官作出如此胆怯的行为使整个团都蒙受羞辱。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('shape', 'n', 'I''m having trouble giving shape to my ideas in this essay.', 1, '2019-10-09 18:49:58', '2019-10-09 18:49:58', '我在这篇文章中表达不清自己的想法。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('shape', 'n', 'In the process it became less amorphous in shape.', 1, '2019-10-09 18:49:58', '2019-10-09 18:49:58', '在过程中它变得较不无定形在形状。');
