INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('share', 'n', 'She''s got all her money in stocks and shares.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '她所有的钱都投放到股票里去了。');
