INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('shout', 'n', 'Her warning shout came too late.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '她发出的警告呼喊声来得太晚了。');
