INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('shout', 'v', 'The children on the sand were shouting with excitement.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '沙滩上的孩子们兴奋得大喊大叫。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('shout', 'v', 'The crowd shouted the speaker down.', 1, '2019-10-09 18:49:56', '2019-10-09 18:49:56', '群众高声喊叫把演讲人的声音压了下去。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('shower', 'n', 'I have received a shower of letters recently.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '最近我收到了一大批信件。');
