INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'n', 'She dispensed with the financial support of her uncle.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '她放弃了她叔叔对她的经济援助。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'n', 'She spoke in support of the project.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '她发言支持这项计划。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'n', 'She was leaning on his arm for support.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '她靠在他的胳膊上寻求支撑。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'n', 'The child is dependent on his parents for support.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '这个小孩靠他的父母养活。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'n', 'The statistics offer further support for our theory.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '这些统计数字为我们的理论提供了进一步的依据。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'n', 'The support has yet to be confirmed.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '助演尚未确定。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'n', 'They hope to win mass support among the middle class.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '他们希望能赢得中产阶级的广泛支持。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'n', 'You have no title to ask for our support.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '你没有资格求我们援助。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'vt', 'He needs a high income to support such a large family.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '他需要有高收入才能供养得起这样一个大家庭。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'vt', 'Her father supported her until she got married.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '她父亲抚养她直到她结婚。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'vt', 'Is this bridge strong enough to support heavy lorries?', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '这座桥禁得住重型卡车通行吗?');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'vt', 'Our company has tied up with another firm to support the show.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '我们公司和另一公司联合资助展览。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'vt', 'The lawyer cite a previous case to support his argument.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '律师引用了以前的案例来支持他的论点。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'vt', 'The pillars support the weight of the roof.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '这些柱子支撑着屋顶的重量。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'vt', 'These findings support my primary diagnosis.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '这些发现证实了我的初步诊断。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'vt', 'They were all on fire to support the disaster area.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '他们都急切地要支援灾区。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'vt', 'They were supported by a local band.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '当地的一支乐队为他们作助兴演出。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'vt', 'We are trying our utmost to support the construction of the border areas.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '我们尽力支援边疆建设。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'vt', 'Will the government provide money to support the arts?', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '政府会拨款资助美术事业吗？');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'vt', 'Will you use your influence to prevail upon them to support the suggestion?', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '你能不能利用你的影响劝他们支持这个建议呢?');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('support', 'vt', 'Your testimony will support his plea of innocence.', 1, '2019-10-09 18:49:54', '2019-10-09 18:49:54', '你的证言将证实他的无罪抗辩。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('suppose', 'vi', 'I suppose he would be about fifty when he obtained a doctorate.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '我推测他获得博士学位时大概是五十岁左右。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('suppose', 'vt', 'I suppose we shall be having some sort of celebration for the bride.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '我想我们还是来为新娘庆贺一番吧。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('suppose', 'vt', 'Let us suppose another planet with conditions similar to those on the earth.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '让我们假定另外有一个具有和地球上相似条件的行星。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('suppose', 'vt', 'Some religions suppose that there is a Heaven physically above the earth.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '一些宗教认为地球上面真有一个天国。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('suppose', 'vt', 'Suppose we dine together.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '让我们一块儿吃饭吧');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('suppose', 'vt', 'Suppose you have a thousand dollars, what will you do with it.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '假设你有一千美元，你会用它做什么。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('suppose', 'vt', 'You are not suppose to stay home during the weekend.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '你不应该周末还呆在家里。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('sure', 'adj', 'Hard work is a sure avenue to success.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '勤奋是可靠的成功之道。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('sure', 'adj', 'He''s not so sure of himself these days.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '他近来不大有自信。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('sure', 'adj', 'I can''t be sure, but I believe it worths trying.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '我不能确定,但我确信这值得一试。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('sure', 'adj', 'I think you are not quite sure about it.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '我认为,你们对这一点并不能完全肯定。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('sure', 'adj', 'I''m sure you''ll want a go when you see it.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '我敢说你看到了就想试一下。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('sure', 'adj', 'The news is sure to overset him.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '这条消息必定会使他心神不安。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('sure', 'adj', 'They had never seen her so sure of herself.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '他们从未看见过她这样自信。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('sure', 'adv', 'I had a feeling we''d get lost, and sure enough, we did.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '我觉得我们迷路了,确实如此,我们迷路了。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('sure', 'adv', 'It sure was a cold night.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '的确是个寒冷的夜晚。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('sure', 'adv', 'Nobody knows the cause of the fire for sure.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '没有人确切地知道大火的原因。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('surface', 'adj', 'This essay is so short that it can only scratch the surface of the topic.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '这篇文章很短，只能对这一问题作肤浅的探讨。');
