INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('surface', 'n', 'The scheme seems on the surface to be quite practical.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '这方案表面上看来似乎很实际。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('surface', 'n', 'The surface of the lake is quite still.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '湖面相当平静。');
