INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('surprise', 'n', 'I must say the news came as a total surprise.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '我得说这个消息来得太突然了。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('surprise', 'n', 'I tried to conceal my surprise when she said she was only 22.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '当她说她只有22岁时,我尽力掩饰自己的惊讶。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('surprise', 'n', 'It was a great surprise to learn of her marriage.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '得知她结婚是一件十分令人惊喜的事。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('surprise', 'n', 'John turned around and looked at me in surprise.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '约翰转过身来,惊奇地望着我。');
