INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('surprise', 'vt', 'It may surprise her greatly to know that Frank has come back from Paris.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '要是知道弗兰克从巴黎回来了,她会惊喜万分的。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('surprise', 'vt', 'It won''t surprise me if they get married very soon.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '即使他们很快就结婚,我也不会感到意外。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('surprise', 'vt', 'Put the presents out of sight so we can surprise her.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '把礼物藏起来,我们给她个惊喜。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('surprise', 'vt', 'The army attacked at night to surprise the rebels.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '军队在夜间发起攻击，把反叛者打了个措手不及。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('surprise', 'vt', 'The news greatly surprised them.', 1, '2019-10-09 18:49:55', '2019-10-09 18:49:55', '这消息使他们大为吃惊。');
