INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('survey', 'n', 'From the top of the hill you can have a survey of the whole city.', 1, '2019-10-09 18:50:01', '2019-10-09 18:50:01', '从山顶上你可以俯瞰整个城市。');
