INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('survey', 'v', 'For this book, the author has surveyed recent developments in linguistics.', 1, '2019-10-09 18:50:01', '2019-10-09 18:50:01', '为了写这本书,作者研究了语言学目前的发展情况。');
