INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('survey', 'v', 'The work of surveying and determining the frontier of all the cities and districts went on smoothly.', 1, '2019-10-09 18:50:01', '2019-10-09 18:50:01', '我省重新勘定各市、区边界的工作进展顺利。');
