INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('survey', 'v', 'They sadly surveyed the scene before them.', 1, '2019-10-09 18:50:01', '2019-10-09 18:50:01', '他们悲伤地环视眼前的情景。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('survey', 'v', 'They surveyed the damage inflicted by the storm.', 1, '2019-10-09 18:50:01', '2019-10-09 18:50:01', '他们调查了暴风雨造成的损失。');
INSERT INTO word_sentence_m (code, type, sentence, status, create_time, update_time, translation) VALUES ('sweater', 'n', 'He put on a thick sweater since it was cold in the morning.', 1, '2019-10-09 18:49:50', '2019-10-09 18:49:50', '早晨很冷，他穿上了一件厚毛衣。');
